import { DebounceInput } from 'react-debounce-input';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { faBook } from '@fortawesome/free-solid-svg-icons';
import Link from 'next/link'
import SearchItem from './SearchItem'

const ForwardedFontAwesomeIcon = React.forwardRef((props, ref) => {
  return <FontAwesomeIcon ref={ref} {...props} />;
});

const SearchBar = (props) => {
  const { value, changeHandler, data, booklist, clickHandler } = props;

  return (
    <div className="PrimarySearch">
      <div className="d-flex">
        <ForwardedFontAwesomeIcon icon={faSearch} className="form-control__Icon" />
        <DebounceInput
          minLength={3}
          debounceTimeout={300}
          value={value}
          className="form-control"
          placeholder="Search for a book..."
          onChange={changeHandler}
        />
        <Link
          href={{
            pathname: '/booklist',
            query: booklist // the book list
          }}
        >
          <ForwardedFontAwesomeIcon icon={faBook} className="form-control__ListIcon" />
        </Link>
      </div>
      {data.length > 0 && (
        <ul className="PrimarySearch-Result shadow-sm">
          {data.map((result) => (
            <li key={result.id} onClick={() => clickHandler(result.id)}>
              <SearchItem
                title={result.volumeInfo.title}
                author={result.volumeInfo.authors && result.volumeInfo.authors}
                publisher={result.volumeInfo.publisher && result.volumeInfo.publisher}
                publishedDate={result.volumeInfo.publishedDate && result.volumeInfo.publishedDate}
              />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchBar;
