import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFolderPlus } from '@fortawesome/free-solid-svg-icons';

const BookListBar = (props) => {
    const classNames = props.showBookListBar ? '' : 'hiddenListBar';
    const booklists = props.booklist;
    const toggleBookListBar = props.toggleBookListBar;
    return(
        <div className={`Booklistbar ${classNames}`}>
            <div className="BooklistbarHead">
                <p>Favourite</p>
                <button 
                onClick={() => addBooklist()}
                className="btn btn-primary"
              ><FontAwesomeIcon icon={faFolderPlus} /></button>
            </div>
            
            {
                booklists.length > 0 &&
                <ul className="PrimarySearch-Result shadow-sm">
                  {
                    booklists.map(result => (
                      <li 
                        key={result.id}
                        onClick={() => clickHandler(result.id)}
                      >
                        <SearchItem title = {result.volumeInfo.title} author = {result.volumeInfo.authors && result.volumeInfo.authors} publisher = {result.volumeInfo.publisher && result.volumeInfo.publisher} publishedDate = {result.volumeInfo.publishedDate && result.volumeInfo.publishedDate} />
                      </li>
                    ))
                  }
                </ul>
            }
        </div>
    )
}

export default BookListBar;
